package decoder

import (
	"encoding/hex"
	"fmt"
	"testing"

	"github.com/btcsuite/btcd/chaincfg"
	scriptDecoder "github.com/mvc-labs/metacontract-script-decoder"
)

func getContractType(codeType uint32) string {
	switch codeType {
	case scriptDecoder.CodeType_FT:
		return "FT"
	case scriptDecoder.CodeType_NFT:
		return "NFT"
	case scriptDecoder.CodeType_UNIQUE:
		return "UNIQUE"
	default:
		return "UNKNOWN"
	}
}

func TestFTContract(t *testing.T) {
	// 测试用例1: 一个有效的FT合约脚本
	param := &chaincfg.MainNetParams
	ftScript := ""
	scriptBytes, err := hex.DecodeString(ftScript)
	if err != nil {
		t.Fatalf("解码脚本失败: %v", err)
	}

	// 检查是否是FT合约
	isFT := IsFTContract(scriptBytes)
	fmt.Printf("是否是FT合约: %v\n", isFT)

	// 提取FT合约信息
	ftInfo, err := ExtractFTInfo(scriptBytes)
	if err != nil {
		t.Fatalf("提取FT信息失败: %v", err)
	}
	if ftInfo != nil {
		fmt.Printf("FT合约信息:\n")
		fmt.Printf("  合约类型: %s\n", getContractType(ftInfo.CodeType))
		fmt.Printf("  CodeHash: %x\n", ftInfo.CodeHash)
		fmt.Printf("  GenesisId: %x\n", ftInfo.GenesisId)
		fmt.Printf("  GenesisID长度: %d\n", ftInfo.GenesisIdLen)
		fmt.Printf("  是否包含地址: %v\n", ftInfo.HasAddress)
		if ftInfo.HasAddress {
			address, err := PkhToAddress(hex.EncodeToString(ftInfo.AddressPkh[:]), param)
			if err != nil {
				fmt.Printf("  地址转换失败: %v\n", err)
			}
			fmt.Printf("  地址: %s\n", address)
		}

		// 打印FT数据
		if ftInfo.FT != nil {
			fmt.Printf("  FT数据:\n")
			fmt.Printf("    SensibleId: %x\n", ftInfo.FT.SensibleId)
			fmt.Printf("    代币名称: %s\n", ftInfo.FT.Name)
			fmt.Printf("    代币符号: %s\n", ftInfo.FT.Symbol)
			fmt.Printf("    代币数量: %d\n", ftInfo.FT.Amount)
			fmt.Printf("    小数位数: %d\n", ftInfo.FT.Decimal)
		}
	}
}

