func TestGetContractType(t *testing.T) {
	// 测试用例1: FT合约
	ftScript := ""
	scriptBytes, err := hex.DecodeString(ftScript)
	if err != nil {
		t.Fatalf("解码脚本失败: %v", err)
	}
	if GetContractType(scriptBytes) != ContractTypeFT {
		t.Errorf("期望是FT合约类型，但得到了其他类型")
	} else {
		fmt.Printf("ContractType: FT\n")
	}

	// 测试用例1: FT合约 genesis
	ftScriptGenesis := ""
	ScriptGenesisBytes, err := hex.DecodeString(ftScriptGenesis)
	if err != nil {
		t.Fatalf("解码脚本失败: %v", err)
	}

	if GetContractType(ScriptGenesisBytes) != ContractTypeFT {
		t.Errorf("期望是FT合约类型，但得到了其他类型")
	} else {
		fmt.Printf("ContractType: FT\n")
	}

	// 测试用例2: Unique合约
	uniqueScript := ""
	scriptBytes, err = hex.DecodeString(uniqueScript)
	if err != nil {
		t.Fatalf("解码脚本失败: %v", err)
	}
	if GetContractType(scriptBytes) != ContractTypeUnique {
		t.Errorf("期望是Unique合约类型，但得到了其他类型")
	} else {
		fmt.Printf("ContractType: Unique\n")
	}

	// 测试用例3: 未知类型
	unknownScript := "76a91429525d5defed63883763355742bc63a9fe80c77188ac"
	scriptBytes, err = hex.DecodeString(unknownScript)
	if err != nil {
		t.Fatalf("解码脚本失败: %v", err)
	}
	if GetContractType(scriptBytes) != ContractTypeUnknown {
		t.Errorf("期望是未知合约类型，但得到了其他类型")
	} else {
		fmt.Printf("ContractType: Unknown\n")
	}
}
